from typing import List
class Solution:
    def exist(self, board: List[List[str]], word: str) -> bool:

        h = len(board)
        if h == 0:
            return False
        w = len(board[0])
        if w == 0:
            return False
        seen = set()

        def bt(i, j, pos):
            if 0 <= i < h and 0 <= j < w and (i, j) not in seen and board[i][j] == word[pos]:
                seen.add((i, j))
                if pos == len(word) - 1:
                    self.ans = True
                    # return True
                else:
                    left = bt(i - 1, j, pos + 1)
                    down = bt(i, j - 1, pos + 1)
                    up = bt(i + 1, j, pos + 1)
                    right = bt(i, j + 1, pos + 1)
                    # return left or right or down or up


            # return False

        self.ans = False
        for i in range(h):
            for j in range(w):
                if board[i][j] == word[0]:
                    bt(i, j, 0)
        return self.ans

